/* @create_hospital.sql */

/* Data about Spanish hospitals (2015) */

/* Tabla hospital */
CREATE TABLE IF NOT EXISTS hospital (idHosp INTEGER PRIMARY KEY,
    nombre CHAR(50),
    direccion CHAR(50),
    provincia CHAR(50),
    tfno CHAR(9),
    email CHAR(100),
    titularidad BOOLEAN); /* 0 = Non Public & 1 = Public */

